import React from "react";

class App extends React.Component {
  state = {
    language: "english",
  };
  onLanguageChange = () => {
    this.setState({ language: "" });
  };
  render() {
    return (
      <div className="ui container">
        <div>
          Select a language:
          <i
            className="flag uk"
            onClick={() => this.onLanguageChange("english")}
          ></i>
          <i
            className="flag fr"
            onClick={() => this.onLanguageChange("french")}
          ></i>
        </div>
      </div>
    );
  }
}

export default App;
